{
  "name": "display.js",
  "path": "backend/src/utils/display.js",
  "imports": [
    "chalk"
  ],
  "mainPurpose": "To display formatted analysis results and project structure information in the console.",
  "type": "module",
  "functions": [
    {
      "name": "displayResults",
      "purpose": "Displays the analysis results including repository information, summary, project structure, call hierarchy, and file analyses.",
      "input": "analysis (object containing repository details, summary, file tree, call hierarchy, and file analyses)",
      "output": "None (logs information to the console)"
    },
    {
      "name": "displayFileTree",
      "purpose": "Recursively displays the file tree structure in a formatted manner.",
      "input": "node (object representing a file or directory), indent (string for indentation)",
      "output": "None (logs file tree structure to the console)"
    }
  ],
  "exports": [
    "displayResults"
  ],
  "dependencies": [
    "chalk"
  ],
  "finalReturnType(s)": "None"
}

{
  "name": "file.js",
  "path": "backend/src/utils/file.js",
  "imports": [
    {
      "module": "fs/promises",
      "imported": ["writeFile"]
    }
  ],
  "mainPurpose": "To save analysis results to a markdown file and corresponding JSON metadata.",
  "type": "module",
  "functions": [
    {
      "name": "saveToFile",
      "purpose": "Saves analysis results in markdown and JSON formats.",
      "input": "filename (string), analysis (object)",
      "output": "Promise<void>"
    },
    {
      "name": "formatFileTree",
      "purpose": "Formats the file tree structure for markdown representation.",
      "input": "node (object), indent (string), repoUrl (string), branch (string), parentPath (string)",
      "output": "string"
    },
    {
      "name": "getFullPath",
      "purpose": "Retrieves the full path of a file or directory node.",
      "input": "node (object)",
      "output": "string"
    }
  ],
  "exports": [
    "saveToFile"
  ],
  "dependencies": [
    "fs/promises"
  ],
  "finalReturnType(s)": "Promise<void>"
}

