{
  "name": "display.js",
  "path": "backend/src/utils/display.js",
  "imports": [
    "chalk"
  ],
  "mainPurpose": "To display formatted analysis results in the console.",
  "type": "module",
  "functions": [
    {
      "name": "displayResults",
      "purpose": "Displays the results of the analysis in a formatted manner.",
      "input": "analysis (object containing repository information, summary, file tree, call hierarchy, and file analyses)",
      "output": "None (logs output to the console)"
    },
    {
      "name": "displayFileTree",
      "purpose": "Recursively displays the file tree structure in a formatted manner.",
      "input": "node (object representing a file or directory), indent (string for indentation)",
      "output": "None (logs output to the console)"
    }
  ],
  "exports": [
    "displayResults"
  ],
  "dependencies": [
    "chalk"
  ],
  "finalReturnType(s)": "None"
}

